/**
 * Basic authentication type class.
 * Used for basic authentication against secured gdb server.
 *
 * @class
 * @author Teodossi Dossev
 */
export class BasicAuthentication {
    /**
     * @override
     * @return {HttpRequestBuilder} requestBuilder
     */
    override getLoginRequestBuilder(): HttpRequestBuilder;
    /**
     * Converts string to Base64
     * @param {String} string
     * @return {string} converted string
     */
    toBase64(string: string): string;
    /**
     * @override
     * @return {string} token
     */
    override getResponseAuthToken(response: any): string;
}
import HttpRequestBuilder = require("../http/http-request-builder");
